        uint256 itemCount = 0;
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < totalItemCount; i++) {
            if (allMarketItems[i + 1].owner == msg.sender) {
                //checking to see if the address of owner is same as caller of this function.
                itemCount += 1;
            }
